import React, {Component} from 'react';

class AddUserComponent extends Component{
    constructor(props){
        super(props);

        this.state = {
            username: '',
            password: '',
            firstName: '',
            lastName: '',
            age: '',
            salary: '',
            message: null
        }
    }

    onChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    saveUser = (e) => {
        e.preventDefault();
        let user = {
            username : this.state.username,
            password : this.state.password,
            firstName : this.state.firstName,
            lastName : this.state.lastName,
            age : this.state.age,
            salary : this.state.salary,   
        }
        ApiService.addUser(user)
        .then(res => {
            this.setState({
                message: user.username + "님이 성공적으로 등록되었습니다"
            })
            console.log(this.state.message);
            this.props.history.push('/users');
        })
        .catch( err => {
            console.log('saveUser() 에러',err);
        })
    }

    render(){
        return(
            <div>
                <h2>Add User</h2>
                <form>
                    <div>
                        <label>User Name : </label>
                        <input type="text" placeholder="별명을 입력하세요" name="username"
                        value={this.state.username} onChange={this.onChange}/>
                    </div>

                    <div>
                        <label>비밀번호 : </label>
                        <input type="password" placeholder="비밀번호을 입력하세요" name="password"
                        value={this.state.password} onChange={this.onChange}/>
                    </div>

                    <div>
                        <label>이름 : </label>
                        <input placeholder="이름을 입력하세요" name="firstName"
                        value={this.state.firstName} onChange={this.onChange}/>
                    </div>

                    <div>
                        <label>성 : </label>
                        <input placeholder="성을 입력하세요" name="lastName"
                        value={this.state.lastName} onChange={this.onChange}/>
                    </div>

                    <div>
                        <label>나이 : </label>
                        <input type="number" placeholder="나이을 입력하세요" name="age"
                        value={this.state.age} onChange={this.onChange}/>
                    </div>

                    <div>
                        <label>키 : </label>
                        <input type="number" placeholder="키를 입력하세요" name="salary"
                        value={this.state.salary} onChange={this.onChange}/>
                    </div>

                    <button onClick={this.saveUser}>Save</button>
                </form>
            </div>
        )
    }
}
export default AddUserComponent;
