import React, {Component} from 'react';
import ApiService from '../../ApiService';


class EditUserComponent extends Component{
    constructor(props){
        super(props);

        this.state = {
            username: '',
            password: '',
            firstName: '',
            lastName: '',
            age: '',
            salary: '',
            message: null
        }
    }

    componentDidMount(){
        this.loadUser();
    }

    loadUser = () => {
        ApiService.fetchByUserID(Window.localStorage.getItem("userID"))
        .then(res => {
            let user = res.data;
            this.setState({
                id: user.id,
                username: user.username,
                firstName: user.firstName,
                lastName: user.lastName,
                age: user.age,
                salary: user.salary
            })
        })
        .catch(err => {
            console.log('loadUser() 에러발생',err);
        })
    }

    onChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    saveUser = (e) => {
        e.preventDefault();

        let user = {
            id: this.state.id,
            password : this.state.password,
            firstName : this.state.firstName,
            lastName : this.state.lastName,
            age : this.state.age,
            salary : this.state.salary,   
        }
        ApiService.editUser(user)
        .then(res => {
            this.setState({
                message: user.firstName + "님의 정보가 성공적으로 수정되었습니다"
            })
            this.props.history.push('/users');
        })
        .catch( err => {
            console.log('saveUser() 에러',err);
        })
    }

    render(){
        return(
            <div>
                <h2>Edit User</h2>
                <form>
                    <div>
                        <label>User Name : </label>
                        <input type="text" name="username" readOnly="true" defaultValue={this.state.username} />
                    </div>

                    <div>
                        <label>이름 : </label>
                        <input placeholder="수정할 이름을 입력하세요" name="firstName"
                        value={this.state.firstName} onChange={this.onChange}/>
                    </div>

                    <div>
                        <label>성 : </label>
                        <input placeholder="수정할 성을 입력하세요" name="lastName"
                        value={this.state.lastName} onChange={this.onChange}/>
                    </div>

                    <div>
                        <label>나이 : </label>
                        <input type="number" placeholder="수정할 나이를 입력하세요" name="age"
                        value={this.state.age} onChange={this.onChange}/>
                    </div>

                    <div>
                        <label>키 : </label>
                        <input type="number" placeholder="수정할 키를 입력하세요" name="salary"
                        value={this.state.salary} onChange={this.onChange}/>
                    </div>

                    <button onClick={this.saveUser}>Save</button>
                </form>
            </div>
        )
    }
}
export default EditUserComponent;
